<?php	// Контроллер	class CronController extends Controller	{		public $defaultAction = "";				// Папка вьюх		protected $_viewsFolder	= "";								/**		 * Обновить очереди задачи.		 * 		 * @time каждые 15 минут		 */		public function actionUpdateTaskQueue()		{				// Получаем все задачи, для которых нужно обновить очереди			$Tasks = Task::findAll([				"condition"	=> "active=1 AND likes=0",			]);						// Обновляем очереди для каждой задачи			foreach ($Tasks as $Task) {				$Task->updateQueue();			}			}				##################################################		###################### AJAX ######################		##################################################							}