<?php	// Контроллер	class MainController extends Controller	{		public $defaultAction = "main";				// Папка вьюх		protected $_viewsFolder	= "main";				public function beforeAction()		{			// Если пользователь забанен, рендерим страницу бана и выходим			if (User::ipBanned()) {				$this->render("banned");				exit();			}  else {				$this->addJs("ng-main");							}		}				// Самый главный ЭКШН		public function actionMain()		{			// Создаем пользователя и загружаем в сессию, если надо			User::createAndLogin();						// Рендерим VIEW (тупо, знаю. просто захотелось написать)			$this->render("vliker");		}				##################################################		###################### AJAX ######################		##################################################			}